import React from 'react';
import ReactDOM from 'react-dom';

const Header = (props) => {
  return <h1>{props.course}</h1>;
};
const Content = (props) => {
  return (
    <p>{props.part1}</p>
  )
}
const Total = (props) => {
  return <p>Number of exercises {props.part1.exercises + props.part2.exercises + props.part2.exercises}</p>
}

const App = () => {
  const course = 'Half Stack application development';
  const part1 = {
    name: 'Fundamentals of React',
    exercises : 10
};
  const part2 = {
    name: 'Using props to pass data',
    exercises: 7
  }
  const part3 = {
    name: 'State of a component',
    exercises: 14
  }
  return (
    <div>
      <Header course={course}/>
      <Content part1={part1}/>
      <Total total={part1.exercises + part2.exercises + props.part2.exercises} />
    </div>
  );
};

ReactDOM.render(<App />, document.getElementById('root'));
